import "./contact.css";
import Phone from "../../img/phone.png";
import Email from "../../img/email.png";
import Gmail from "../../img/gmail.png";
import Address from "../../img/address.png";
import React, { useRef, useState, useEffect } from "react";
import emailjs from 'emailjs-com';

const Contact = () => {
    const formRef = useRef()
    const [done, setDone] = useState(false);
    const [showModal, setShowModal] = useState(false)

    const sendEmail = (e) =>{
        e.preventDefault();

        emailjs.sendForm('service_jclm1rl', 'template_mxb0gvl', formRef.current, 'QorUSfyGMetQIKlrc') // You need to pay using emailjs to actually send the email
            .then((result) => {
                console.log(result.text);
                setDone(true);
            }, (error) => {
                console.log(error.text);
            });
    };

    const Modal = props => {
        const [container, setContainer] = useState();

        useEffect(() => {
            const container = document.createElement("div");
            setContainer(container);
            document.appendChild(container);
        }, []);

        return(
            React.createPortal(
                props.children,
                container,
            )
        );
    }

    return (
        <div className="c">
            <div className="c-bg"></div>
            <div className="c-wrapper">
                <div className="c-left">
                    <h1 className="c-title">Let's discuss the opportunity</h1>
                    <div className="c-info">
                        <div className="c-info-item">
                            <img
                                src={Phone}
                                alt=""
                                className="c-icon"
                            />
                            (647)-619-0713
                        </div>

                        <div className="c-info-item">
                            <img
                                src={Email}
                                alt=""
                                className="c-icon"
                            />
                            aran0713@hotmail.com
                        </div>

                        <div className="c-info-item">
                            <img
                                src={Gmail}
                                alt=""
                                className="c-icon"
                            />
                            aran.abrahamlingam@gmail.com
                        </div>

                        <div className="c-info-item">
                            <img
                                src={Address}
                                alt=""
                                className="c-icon"
                            />
                            134 Fitzgibbon Ave Scarborough, ON M1K 4A7

                        </div>
                    </div>
                </div>


                <div className="c-right">
                    <p className="c-desc">
                    Please fill out the form. I will get back to you as soon as possible. 
                    </p>

                    <form ref={formRef} onSubmit={sendEmail}>
                        <input type="text" placeholder="Name" name="user_name" />
                        <input type="text" placeholder="Subject" name="user_subject" />
                        <input type="text" placeholder="Email" name="user_email" />
                        <textarea rows="5" placeholder="Message" name="message"/>
                        <button onClick = {() => setShowModal(true)}>
                            Submit
                        </button>
                        {done && " Thanks you"}

                        {showModal && 
                            <Modal onClose={() => setShowModal(false)}>
                                {closeModal => 
                                    <div>
                                        <input/>
                                        <button onClick={closeModal}>
                                            Submit
                                        </button>
                                    </div>
                                }
                            </Modal>
                        }
                        
                    </form>
                    
                </div>
            </div>
        </div>
    )
} 

export default Contact
